import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertOrderSchema, updateOrderSchema, type AttachedFile } from "@shared/schema";
import { sendOrderConfirmationEmail, sendOrderNotificationEmail } from "./services/email";
import { sendOrderNotificationToSlack } from "./services/slack";
import multer from "multer";
import path from "path";

const upload = multer({
  dest: 'uploads/',
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['.csv', '.json', '.pdf', '.png', '.jpg', '.jpeg'];
    const ext = path.extname(file.originalname).toLowerCase();
    if (allowedTypes.includes(ext)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Order management routes
  app.post("/api/orders", upload.array('files', 5), async (req, res) => {
    try {
      console.log('Request body:', req.body);
      console.log('Request files:', req.files);

      // Parse JSON fields that come as strings from FormData
      const parsedBody = { ...req.body };
      if (parsedBody.integrations && typeof parsedBody.integrations === 'string') {
        try {
          parsedBody.integrations = JSON.parse(parsedBody.integrations);
        } catch (e) {
          console.error('Failed to parse integrations:', e);
        }
      }
      if (parsedBody.hasCredentials && typeof parsedBody.hasCredentials === 'string') {
        try {
          parsedBody.hasCredentials = JSON.parse(parsedBody.hasCredentials);
        } catch (e) {
          console.error('Failed to parse hasCredentials:', e);
        }
      }

      const orderData = insertOrderSchema.parse(parsedBody);

      // Handle file uploads
      const files = req.files as Express.Multer.File[];
      const attachedFiles: AttachedFile[] = files?.map(file => ({
        originalName: file.originalname,
        filename: file.filename,
        path: file.path,
        size: file.size,
        mimetype: file.mimetype
      })) || [];

      const order = await storage.createOrder({
        ...orderData,
        attachedFiles,
      });

      // Send notifications
      try {
        await Promise.all([
          sendOrderConfirmationEmail(order.email, order.fullName, order.orderId),
          sendOrderNotificationEmail(['svimonishvilitoka@gmail.com', 'giorginatsvlishvili2010@gmail.com'], order),
          sendOrderNotificationToSlack(order)
        ]);
      } catch (notificationError) {
        console.error('Notification error:', notificationError);
        // Don't fail the order creation if notifications fail
      }

      res.json({ success: true, orderId: order.orderId });
    } catch (error) {
      console.error('Order creation error:', error);
      res.status(400).json({ error: error instanceof Error ? error.message : 'Invalid order data' });
    }
  });

  app.get("/api/orders", async (req, res) => {
    try {
      const orders = await storage.getAllOrders();
      res.json(orders);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch orders' });
    }
  });

  app.get("/api/orders/:id", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.id);
      if (!order) {
        return res.status(404).json({ error: 'Order not found' });
      }
      res.json(order);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch order' });
    }
  });

  app.get("/api/orders/by-order-id/:orderId", async (req, res) => {
    try {
      const order = await storage.getOrderByOrderId(req.params.orderId);
      if (!order) {
        return res.status(404).json({ error: 'Order not found' });
      }
      res.json(order);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch order' });
    }
  });

  app.patch("/api/orders/:id", async (req, res) => {
    try {
      const updates = updateOrderSchema.parse(req.body);
      const order = await storage.updateOrder(req.params.id, updates);
      if (!order) {
        return res.status(404).json({ error: 'Order not found' });
      }
      res.json(order);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Invalid update data' });
    }
  });

  app.delete("/api/orders/:id", async (req, res) => {
    try {
      const success = await storage.deleteOrder(req.params.id);
      if (!success) {
        return res.status(404).json({ error: 'Order not found' });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to delete order' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Order, type InsertOrder, type UpdateOrder } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Order management
  createOrder(order: InsertOrder): Promise<Order>;
  getOrder(id: string): Promise<Order | undefined>;
  getOrderByOrderId(orderId: string): Promise<Order | undefined>;
  getAllOrders(): Promise<Order[]>;
  updateOrder(id: string, updates: UpdateOrder): Promise<Order | undefined>;
  deleteOrder(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private orders: Map<string, Order>;

  constructor() {
    this.users = new Map();
    this.orders = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createOrder(insertOrder: InsertOrder): Promise<Order> {
    const id = randomUUID();
    const orderId = `ORD-${new Date().getFullYear()}-${String(this.orders.size + 1).padStart(4, '0')}`;
    const now = new Date();

    const order: Order = {
      ...insertOrder,
      id,
      orderId,
      status: "new" as const,
      adminNotes: null,
      createdAt: now,
      updatedAt: now,
    };

    this.orders.set(id, order);
    return order;
  }

  async getOrder(id: string): Promise<Order | undefined> {
    return this.orders.get(id);
  }

  async getOrderByOrderId(orderId: string): Promise<Order | undefined> {
    return Array.from(this.orders.values()).find(
      (order) => order.orderId === orderId,
    );
  }

  async getAllOrders(): Promise<Order[]> {
    return Array.from(this.orders.values()).sort(
      (a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime()
    );
  }

  async updateOrder(id: string, updates: UpdateOrder): Promise<Order | undefined> {
    const existingOrder = this.orders.get(id);
    if (!existingOrder) return undefined;

    const updatedOrder: Order = {
      ...existingOrder,
      ...updates,
      updatedAt: new Date(),
    };

    this.orders.set(id, updatedOrder);
    return updatedOrder;
  }

  async deleteOrder(id: string): Promise<boolean> {
    return this.orders.delete(id);
  }
}

export const storage = new MemStorage();

