client/src/components/admin/order-detail.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Loader2, 
  User, 
  Mail, 
  Building, 
  Calendar, 
  FileText, 
  Download,
  MessageSquare,
  Clock,
  ExternalLink
} from "lucide-react";
import { formatDistance, format } from "date-fns";

interface OrderDetailProps {
  orderId: string;
}

export default function OrderDetail({ orderId }: OrderDetailProps) {
  const [adminNotes, setAdminNotes] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: order, isLoading } = useQuery({
    queryKey: ['/api/orders', orderId],
    enabled: !!orderId,
  });

  const updateOrderMutation = useMutation({
    mutationFn: async (data: { status?: string; adminNotes?: string }) => {
      return apiRequest("PATCH", `/api/orders/${orderId}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/orders'] });
      queryClient.invalidateQueries({ queryKey: ['/api/orders', orderId] });
      toast({
        title: "Success",
        description: "Order updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update order",
        variant: "destructive",
      });
    },
  });

  const handleStatusUpdate = (newStatus: string) => {
    updateOrderMutation.mutate({ status: newStatus });
  };

  const handleNotesUpdate = () => {
    updateOrderMutation.mutate({ adminNotes });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <Loader2 className="w-6 h-6 animate-spin" />
      </div>
    );
  }

  if (!order) {
    return (
      <div className="text-center py-8 text-muted-foreground">
        Order not found
      </div>
    );
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case "new": return "bg-blue-100 text-blue-800";
      case "in_review": return "bg-yellow-100 text-yellow-800";
      case "in_progress": return "bg-orange-100 text-orange-800";
      case "delivered": return "bg-green-100 text-green-800";
      case "closed": return "bg-gray-100 text-gray-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "new": return "New";
      case "in_review": return "In Review";
      case "in_progress": return "In Progress";
      case "delivered": return "Delivered";
      case "closed": return "Closed";
      default: return status;
    }
  };

  const getAutomationTypeLabel = (type: string) => {
    switch (type) {
      case "whatsapp_chatbot": return "WhatsApp Chatbot";
      case "crm_integration": return "CRM Integration";
      case "email_automation": return "Email Automation";
      case "file_sync": return "File Sync / ETL";
      case "custom_workflow": return "Custom Workflow";
      default: return type;
    }
  };

  return (
    <div className="space-y-6">
      {/* Order Header */}
      <div className="space-y-2">
        <div className="flex items-center justify-between">
          <h3 className="font-semibold text-lg">{order.orderId}</h3>
          <Badge className={getStatusColor(order.status)}>
            {getStatusLabel(order.status)}
          </Badge>
        </div>
        <p className="text-sm text-muted-foreground flex items-center gap-2">
          <Calendar className="w-4 h-4" />
          Created {formatDistance(new Date(order.createdAt), new Date(), { addSuffix: true })}
          {order.updatedAt !== order.createdAt && (
            <span>
              â€¢ Updated {formatDistance(new Date(order.updatedAt), new Date(), { addSuffix: true })}
            </span>
          )}
        </p>
      </div>

      <Separator />

      {/* Customer Info */}
      <div className="space-y-3">
        <h4 className="font-medium flex items-center gap-2">
          <User className="w-4 h-4" />
          Customer Information
        </h4>
        <div className="space-y-2 text-sm">
          <div className="flex items-center gap-2">
            <span className="font-medium">Name:</span>
            {order.fullName}
          </div>
          <div className="flex items-center gap-2">
            <Mail className="w-4 h-4 text-muted-foreground" />
            <a href={`mailto:${order.email}`} className="text-primary hover:underline">
              {order.email}
            </a>
          </div>
          {order.company && (
            <div className="flex items-center gap-2">
              <Building className="w-4 h-4 text-muted-foreground" />
              {order.company}
            </div>
          )}
        </div>
      </div>

      <Separator />

      {/* Project Details */}
      <div className="space-y-3">
        <h4 className="font-medium flex items-center gap-2">
          <FileText className="w-4 h-4" />
          Project Details
        </h4>
        <div className="space-y-2 text-sm">
          <div>
            <span className="font-medium">Project Name:</span><br />
            {order.projectName}
          </div>
          <div>
            <span className="font-medium">Automation Type:</span><br />
            {getAutomationTypeLabel(order.automationType)}
          </div>
          {order.customDescription && (
            <div>
              <span className="font-medium">Custom Description:</span><br />
              <div className="bg-muted p-2 rounded text-muted-foreground mt-1">
                {order.customDescription}
              </div>
            </div>
          )}
          {order.integrations && order.integrations.length > 0 && (
            <div>
              <span className="font-medium">Integrations:</span><br />
              <div className="flex flex-wrap gap-1 mt-1">
                {order.integrations.map((integration: string, index: number) => (
                  <Badge key={index} variant="outline" className="text-xs">
                    {integration}
                  </Badge>
                ))}
              </div>
            </div>
          )}
          {order.deliverySpeed && (
            <div>
              <span className="font-medium">Delivery Speed:</span><br />
              {order.deliverySpeed === 'standard' ? 'Standard (7-14 days)' : 'Fast (3-5 days)'}
            </div>
          )}
          {order.priorityNotes && (
            <div>
              <span className="font-medium">Priority Notes:</span><br />
              <div className="bg-muted p-2 rounded text-muted-foreground mt-1">
                {order.priorityNotes}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Files */}
      {order.attachedFiles && order.attachedFiles.length > 0 && (
        <>
          <Separator />
          <div className="space-y-3">
            <h4 className="font-medium flex items-center gap-2">
              <Download className="w-4 h-4" />
              Attached Files
            </h4>
            <div className="space-y-2">
              {order.attachedFiles.map((file: any, index: number) => (
                <div key={index} className="flex items-center justify-between p-2 bg-muted rounded">
                  <div className="flex items-center gap-2">
                    <FileText className="w-4 h-4 text-muted-foreground" />
                    <span className="text-sm">{file.originalName}</span>
                    <Badge variant="outline" className="text-xs">
                      {Math.round(file.size / 1024)}KB
                    </Badge>
                  </div>
                  <Button size="sm" variant="outline" data-testid={`download-file-${index}`}>
                    <Download className="w-3 h-3" />
                  </Button>
                </div>
              ))}
            </div>
          </div>
        </>
      )}

      {/* External Links */}
      {order.exampleLink && (
        <>
          <Separator />
          <div className="space-y-3">
            <h4 className="font-medium flex items-center gap-2">
              <ExternalLink className="w-4 h-4" />
              External Links
            </h4>
            <div>
              <a 
                href={order.exampleLink} 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-primary hover:underline text-sm flex items-center gap-1"
                data-testid="external-link"
              >
                {order.exampleLink}
                <ExternalLink className="w-3 h-3" />
              </a>
            </div>
          </div>
        </>
      )}

      <Separator />

      {/* Status Update */}
      <div className="space-y-3">
        <h4 className="font-medium">Update Status</h4>
        <Select value={order.status} onValueChange={handleStatusUpdate}>
          <SelectTrigger data-testid="select-order-status">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="new">New</SelectItem>
            <SelectItem value="in_review">In Review</SelectItem>
            <SelectItem value="in_progress">In Progress</SelectItem>
            <SelectItem value="delivered">Delivered</SelectItem>
            <SelectItem value="closed">Closed</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Admin Notes */}
      <div className="space-y-3">
        <h4 className="font-medium flex items-center gap-2">
          <MessageSquare className="w-4 h-4" />
          Admin Notes
        </h4>
        <Textarea
          placeholder="Add internal notes about this order..."
          value={adminNotes || order.adminNotes || ""}
          onChange={(e) => setAdminNotes(e.target.value)}
          className="min-h-[100px]"
          data-testid="textarea-admin-notes"
        />
        <Button
          onClick={handleNotesUpdate}
          disabled={updateOrderMutation.isPending}
          size="sm"
          data-testid="button-save-notes"
        >
          {updateOrderMutation.isPending ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Saving...
            </>
          ) : (
            "Save Notes"
          )}
        </Button>
      </div>

      {/* Action Buttons */}
      <div className="flex gap-2 pt-4">
        <Button 
          variant="outline" 
          size="sm"
          onClick={() => window.open(`mailto:${order.email}?subject=Your Order ${order.orderId}`, '_blank')}
          data-testid="button-email-customer"
        >
          <Mail className="w-4 h-4 mr-2" />
          Email Customer
        </Button>

        <Button 
          variant="outline" 
          size="sm"
          onClick={() => {
            const orderDetails = `Order ID: ${order.orderId}\nCustomer: ${order.fullName}\nProject: ${order.projectName}\nStatus: ${getStatusLabel(order.status)}`;
            navigator.clipboard.writeText(orderDetails);
            toast({
              title: "Copied",
              description: "Order details copied to clipboard",
            });
          }}
          data-testid="button-copy-details"
        >
          Copy Details
        </Button>
      </div>
    </div>
  );
}

client/src/components/admin/order-list.t.content:
import { useState } from "react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Search, Calendar, User, Building } from "lucide-react";
import { formatDistance } from "date-fns";

interface Order {
  id: string;
  orderId: string;
  fullName: string;
  email: string;
  company?: string;
  projectName: string;
  automationType: string;
  status: string;
  createdAt: string;
}

interface OrderListProps {
  orders: Order[];
  onSelectOrder: (orderId: string) => void;
  selectedOrderId: string | null;
}

export default function OrderList({ orders, onSelectOrder, selectedOrderId }: OrderListProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [sortBy, setSortBy] = useState("newest");

  // Filter and sort orders
  const filteredOrders = orders
    .filter(order => {
      const matchesSearch = 
        order.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.orderId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.projectName.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesStatus = statusFilter === "all" || order.status === statusFilter;

      return matchesSearch && matchesStatus;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
        case "oldest":
          return new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime();
        case "name":
          return a.fullName.localeCompare(b.fullName);
        default:
          return 0;
      }
    });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "new": return "bg-blue-100 text-blue-800";
      case "in_review": return "bg-yellow-100 text-yellow-800";
      case "in_progress": return "bg-orange-100 text-orange-800";
      case "delivered": return "bg-green-100 text-green-800";
      case "closed": return "bg-gray-100 text-gray-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "new": return "New";
      case "in_review": return "In Review";
      case "in_progress": return "In Progress";
      case "delivered": return "Delivered";
      case "closed": return "Closed";
      default: return status;
    }
  };

  const getAutomationTypeLabel = (type: string) => {
    switch (type) {
      case "whatsapp_chatbot": return "WhatsApp Bot";
      case "crm_integration": return "CRM Integration";
      case "email_automation": return "Email Automation";
      case "file_sync": return "File Sync";
      case "custom_workflow": return "Custom Workflow";
      default: return type;
    }
  };

  return (
    <div className="space-y-4">
      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
          <Input
            placeholder="Search by name, email, order ID, or project..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
            data-testid="input-search-orders"
          />
        </div>

        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-full sm:w-[180px]" data-testid="select-status-filter">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="new">New</SelectItem>
            <SelectItem value="in_review">In Review</SelectItem>
            <SelectItem value="in_progress">In Progress</SelectItem>
            <SelectItem value="delivered">Delivered</SelectItem>
            <SelectItem value="closed">Closed</SelectItem>
          </SelectContent>
        </Select>

        <Select value={sortBy} onValueChange={setSortBy}>
          <SelectTrigger className="w-full sm:w-[180px]" data-testid="select-sort-by">
            <SelectValue placeholder="Sort by" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="newest">Newest First</SelectItem>
            <SelectItem value="oldest">Oldest First</SelectItem>
            <SelectItem value="name">Name A-Z</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Orders List */}
      <div className="space-y-2 max-h-[600px] overflow-y-auto">
        {filteredOrders.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            No orders found matching your criteria
          </div>
        ) : (
          filteredOrders.map((order) => (
            <Card
              key={order.id}
              className={`cursor-pointer transition-all duration-200 hover:shadow-md ${
                selectedOrderId === order.id ? 'ring-2 ring-primary' : ''
              }`}
              onClick={() => onSelectOrder(order.id)}
              data-testid={`order-card-${order.orderId}`}
            >
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-2">
                  <div className="flex items-center gap-2">
                    <span className="font-semibold text-foreground">
                      {order.orderId}
                    </span>
                    <Badge className={getStatusColor(order.status)}>
                      {getStatusLabel(order.status)}
                    </Badge>
                  </div>
                  <span className="text-xs text-muted-foreground">
                    {formatDistance(new Date(order.createdAt), new Date(), { addSuffix: true })}
                  </span>
                </div>

                <div className="space-y-1">
                  <div className="flex items-center gap-2 text-sm">
                    <User className="w-4 h-4 text-muted-foreground" />
                    <span className="font-medium">{order.fullName}</span>
                    <span className="text-muted-foreground">({order.email})</span>
                  </div>

                  {order.company && (
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <Building className="w-4 h-4" />
                      <span>{order.company}</span>
                    </div>
                  )}

                  <div className="text-sm">
                    <span className="font-medium text-foreground">Project:</span>{' '}
                    {order.projectName}
                  </div>

                  <div className="text-sm">
                    <span className="font-medium text-foreground">Type:</span>{' '}
                    {getAutomationTypeLabel(order.automationType)}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/components/animations/workflo.content:
import { useEffect, useRef } from "react";

export default function WorkflowBackground() {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    const svg = svgRef.current;
    if (!svg) return;

    // Add animation classes to paths after mount
    const paths = svg.querySelectorAll('path');
    paths.forEach((path, index) => {
      path.style.animationDelay = `${index * 0.5}s`;
      path.classList.add('animate-pulse');
    });

    const circles = svg.querySelectorAll('circle');
    circles.forEach((circle, index) => {
      circle.style.animationDelay = `${index * 0.3}s`;
      circle.classList.add('animate-pulse');
    });
  }, []);

  return (
    <div className="workflow-animation">
      <svg 
        ref={svgRef}
        width="100%" 
        height="100%" 
        viewBox="0 0 1200 600" 
        className="absolute inset-0"
      >
        <defs>
          <linearGradient id="nodeGradient" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" stopColor="hsl(217 91% 35%)" stopOpacity="0.8"/>
            <stop offset="100%" stopColor="hsl(262 83% 58%)" stopOpacity="0.8"/>
          </linearGradient>
        </defs>

        {/* Connection lines */}
        <g stroke="url(#nodeGradient)" strokeWidth="2" fill="none">
          <path d="M100,200 Q300,150 500,200 T900,250" />
          <path d="M150,350 Q400,300 650,350 T1000,400" />
          <path d="M200,100 Q500,50 800,100" />
        </g>

        {/* Workflow nodes */}
        <g fill="url(#nodeGradient)">
          <circle cx="100" cy="200" r="8" />
          <circle cx="500" cy="200" r="10" />
          <circle cx="900" cy="250" r="8" />
          <circle cx="150" cy="350" r="6" />
          <circle cx="650" cy="350" r="10" />
          <circle cx="1000" cy="400" r="8" />
        </g>
      </svg>
    </div>
  );
}

